mapper.insert:
INSERT INTO board
    ( board_title
    , board_content
    , frst_reg_user_id
    , frst_reg_user_ip_addr
    , frst_reg_dttm
    , last_upd_user_id
    , last_upd_user_ip_addr
    , last_upd_dttm)
VALUES
    ( #{boardTitle}
    , #{boardContent}
    , #{frstRegUserId}
    , #{frstRegUserIpAddr}
    , NOW()
    , #{lastUpdUserId}
    , #{lastUpdUserIpAddr}
    , NOW());

mapper.update:
UPDATE board

    , last_upd_user_id = #{lastUpdUserId}
    , last_upd_dttm = NOW()

        , last_upd_user_ip_addr = #{lastUpdUserIpAddr}


        , board_title = #{boardTitle}


        , board_content = #{boardContent}


WHERE board_id = #{boardId}

UPDATE board

    , last_upd_user_id = #{lastUpdUserId}
    , last_upd_dttm = NOW()

        , last_upd_user_ip_addr = #{lastUpdUserIpAddr}

    , del_yn = true

WHERE board_id = #{boardId}

